<main class="content">    <header class="header-box">        <h2 class="header-box-h">Gerencie todos os uploads do sistema</h2>        <div class="header-box-btn">            <a class="btn-default btn-blue" href="?exe=uploads/create"><span class="lnr lnr-plus-circle"></span> Novo upload</a>        </div>    </header>    <div class="box">        <table id="table-upload" class="table table-striped table-bordered" style="width:100%">            <thead>                <tr>                    <th><span class="lnr lnr-graduation-hat"></span> Nome</th>                    <th><span class="lnr lnr-upload"></span> Upload</th>                    <th><span class="lnr lnr-text-align-left"></span> Descrição</th>                    <th></th>                </tr>            </thead>            <tfoot>                <tr>                    <th><span class="lnr lnr-graduation-hat"></span> Nome</th>                    <th><span class="lnr lnr-upload"></span> Upload</th>                    <th><span class="lnr lnr-text-align-left"></span> Descrição</th>                    <th></th>                </tr>             </tfoot>        </table>    </div>    <?php    $Download = filter_input(INPUT_GET, 'action', FILTER_DEFAULT);//    echo $Download;    if (isset($Download)):        $Read = new Read;        $Read->ExeRead("tb_uploads", "WHERE name_up = :n LIMIT 1", "n={$Download}");        extract($Read->getResult()[0]);        $arquivoLocal = 'uploads/' . $Upload;        echo $arquivoLocal;        if (!file_exists($arquivoLocal)) {            echo "Arquivo não existente!";            exit;        } else {            echo "Arquivo existe!";        }        $fsize = filesize($arquivoLocal);        $path_parts = pathinfo($arquivoLocal);        $ext = strtolower($path_parts["extension"]);        switch ($ext) {            case "pdf": $ctype = "application/pdf";                break;            case "exe": $ctype = "application/octet-stream";                break;            case "zip": $ctype = "application/zip";                break;            case "doc": $ctype = "application/msword";                break;            case "xls": $ctype = "application/vnd.ms-excel";                break;            case "ppt": $ctype = "application/vnd.ms-powerpoint";                break;            case "gif": $ctype = "image/gif";                break;            case "png": $ctype = "image/png";                break;            case "jpeg":            case "jpg": $ctype = "image/jpg";                break;            default: $ctype = "application/force-download";        }        header("Pragma: public"); // required        header("Expires: 0");        header("Cache-Control: must-revalidate, post-check=0, pre-check=0");        header("Cache-Control: private", false); // required for certain browsers        header("Content-Type: $ctype");        header("Content-Disposition: attachment; filename=\"" . basename($arquivoLocal) . "\";");        header("Content-Transfer-Encoding: binary");        header("Content-Length: " . $fsize);        ob_end_clean();        flush();        readfile($arquivoLocal);        echo "Baixando arquivo!";    endif;    ?></main>